define('icons', $icons);

function icons($name, $class = "w-5 h-5", $type = "0")
{
  $icon = icons[$name];
  $icon = str_replace("{class}", $class, $icon);

  if ($type == "0") {
    echo $icon;
  } else {
    return $icon;
  }
}